package datastorage

import (
	"crypto/md5"
	"fmt"
	"strconv"
	"testing"
)

func TestCreateUser(t *testing.T) {
	InitDataStore("test:test@tcp(localhost:3306)/test")
	psw := fmt.Sprintf("%x", md5.Sum([]byte("helloworld")))
	avatar := "data:image/jpeg;base64,"
	for i := 10000000; i < 10000000+10000000; i++ {
		uname := strconv.FormatInt((int64)(i), 10)
		nname := fmt.Sprintf("用户%d", i)
		GetInstance().CreateUser(uname, psw, nname, avatar)
	}
}
